styles:
wk-multiselect .dropbtn {
  padding: 8px;
  font-size: 16px;
  border: none;
  cursor: pointer;
  width: 200px;
  border: 1px solid #ddd;
  border-bottom: 1px solid #ddd;
}
wk-multiselect #myInput {
  background-position: 14px 12px;
  background-repeat: no-repeat;
  font-size: 16px;
  padding: 2px 20px 2px 13px;
  width: 235px;
  border: none;
}
wk-multiselect .dropdown {
  position: relative;
}
wk-multiselect .dropdown-toggle::after {
  float: right;
  margin: 5px;
  display:none;

}
wk-multiselect .dropdowncontent {
  position: absolute;
  z-index: 1;
  border-radius: 0 !important;
  border-bottom: 1px solid #ddd;
  padding: 0;
}
wk-multiselect .scolldiv {
  overflow: auto;
  max-height: 200px;
  cursor: pointer;
}
wk-multiselect .scolldiv p:hover {
  background-color: #efefef;
}
wk-multiselect p {
  padding: 1px 5px;
  text-decoration: none;
  display: block;
  cursor: pointer;
}
wk-multiselect .cursor {
  cursor: pointer;
}
wk-multiselect .dropdowncontent .cursor {
  border-bottom: 2px solid #ddd;
  border-top: 2px solid #ddd;
}
wk-multiselect p {
  margin-bottom: 0rem;
}
wk-multiselect label {
  padding: 2px 5px;
  cursor: pointer;
  margin: 0rem;
}
wk-multiselect label .highlight {
  font-weight: bold;
  padding: 0px;
}
wk-multiselect .searchicon {
  margin: -1px 8px 0px -24px;
}
wk-multiselect .dropdowm-menu {
  padding: 0 !important;
}
@media screen and (max-width: 768px) {
  .dropdowm-menu {
    border-radius: 0;
  }
}
::-webkit-scrollbar-track {
  -webkit-box-shadow: inset 0 0 0px rgba(255, 255, 255, 1);
  border-radius: 3px;
}
::-webkit-scrollbar-thumb {
  border-radius: 3px;
  -webkit-box-shadow: inset 0 0 3px rgba(255, 255, 255, 1);
}
.dropdowm-menu {
  padding: 0 !important;
}
.left-inner-addon {
  position: relative;
}
.left-inner-addon input {
  padding-left: 30px;
}
.left-inner-addon i {
  position: absolute;
  padding: 10px 12px;
  pointer-events: none;
}
.right-inner-addon {
  position: relative;
}
.right-inner-addon input {
  padding-right: 30px;
}
.right-inner-addon i {
  position: absolute;
  right: 0px;
  padding: 0px 16px;
  pointer-events: none;
}
::-webkit-scrollbar-track {
  -webkit-box-shadow: inset 0 0 0px rgba(255, 255, 255, 1);
  border-radius: 3px;
}

::-webkit-scrollbar-thumb {
  border-radius: 3px;
  -webkit-box-shadow: inset 0 0 3px rgba(255, 255, 255, 1);
}

/* width */
::-webkit-scrollbar {
  width: 10px;
}
/* Track */
::-webkit-scrollbar-track {
  box-shadow: inset 0 0 5px grey;
  border-radius: 10px;
}
/* Handle */
::-webkit-scrollbar-thumb {
  background: gray;
  border-radius: 10px;
}
/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: gray;
}
input[type="checkbox"] {
  cursor: pointer !important;
}
::-webkit-input-placeholder {
  font-style: italic !important;
}
:-moz-placeholder {
  font-style: italic !important;
}
::-moz-placeholder {
  font-style: italic !important;
}
:-ms-input-placeholder {
  font-style: italic !important;
}
